import React, { useState, useEffect, useContext } from "react";
import $ from "jquery";
import { Recorder } from 'react-voice-recorder'
import 'react-voice-recorder/dist/index.css'
import Button from '@material-ui/core/Button';
import Slider from 'react-rangeslider';
import Switch from "react-switch";
import dateFormat from "dateformat";
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import axios from "axios";
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogContent from '@material-ui/core/DialogContent';
import banner from "../../../images/vtc-banner.jpg";
import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import {
    MuiPickersUtilsProvider,
    KeyboardTimePicker,
    KeyboardDatePicker,
} from '@material-ui/pickers';
import up_img from "../../../images/up.png";
import middle_img from "../../../images/middle.png";
import bottom_img from "../../../images/bottom.png";
import man from "../../../images/man.png";
import house_img from "../../../images/house-img.jpg";
import CancelIcon from '@material-ui/icons/Cancel';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import AddIcon from '@material-ui/icons/Add';
import SaveIcon from '@material-ui/icons/Save';
import Footer from "../../../components/Footer/AgentFooter";
import AgentHeader from "../Header/AgentHeader";
import { Link, useHistory } from "react-router-dom";
import { AuthContext } from "../../../CommonMethods/Authentication";
import { APIURL, APIPath } from "../../../CommonMethods/Fetch";
import { postRecord } from "../../../CommonMethods/Save";
import { makeStyles } from '@material-ui/core/styles';
import Skeleton from '@material-ui/lab/Skeleton';
import CircularProgress from '@material-ui/core/CircularProgress';
import Backdrop from '@material-ui/core/Backdrop';
const APIGetEzFlashCard = APIURL() + "get-flashvideo";
const APICreateFlashCard = APIURL() + "create-flashvideo";
const APICreateSubscribeFlashVideo = "create-subscribe-flashvideo";


export default function AgentEzFlashCard(props) {
    const tourId = props.match.params.flashid;
    function Alert(props) {
        return <MuiAlert elevation={6} variant="filled" {...props} />;
    }
    const useStyles = makeStyles((theme) => ({
        backdrop: {
            zIndex: theme.zIndex.drawer + 9999,
            color: '#fff',
        },
    }));
    let history = useHistory();
    const { dispatch } = useContext(AuthContext);
    const classes = useStyles();
    const context = useContext(AuthContext);
    const [sync, setSync] = useState(false);
    const [openWarning, setOpenWarning] = useState(false);
    const [open, setOpen] = useState(false);
    const [openError, setOpenError] = useState(false);
    const [openSuccess, setOpenSuccess] = useState(false);
    const [message, setMessage] = useState("");
    const [flashcardData, setFlashCardData] = useState([]);
    const [flashcardDatavalue, setFlashCardDatavalue] = useState({});
    const [openVideoModal, setOpenVideoModal] = useState(false);
    const [maxWidth, setMaxWidth] = React.useState('lg');
    const [videoUrl, setVideoUrl] = useState("");
    const [videoDownload, setDownloadVideo] = useState("");
    // const [progressBar, setProgressBar] = useState(false);

    useEffect(() => {
        $(".gee_cross").hide();
        $(".gee_menu").hide();
    }, []);
    const ShowMenu = () => {
        $(".gee_menu").slideToggle("slow", function () {
            $(".gee_hamburger").hide();
            $(".gee_cross").show();
        });
    }
    const HideMenu = () => {
        $(".gee_menu").slideToggle("slow", function () {
            $(".gee_cross").hide();
            $(".gee_hamburger").show();
        });
    }
    const handleClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenSuccess(false);
        setOpenError(false);
        setOpenVideoModal(false);
    };

    useEffect(() => {
        if (context.state.user) {
            const objusr = { authenticate_key: "abcd123XYZ", agentId: JSON.parse(context.state.user).agentId, tourid: tourId };
            postRecord(APIGetEzFlashCard, objusr)
                .then(res => {
                    if (res.data[0].response.status === "success") {
                        setFlashCardData(res.data[0].response.data);
                    }
                });
        }
    }, [sync, context.state.user, tourId]);
    const setVideoModal = (video) => {
        setOpenVideoModal(true);
        setVideoUrl(video);

    }

    const createEzFlashCard = (flash_data, id, is_dwnld) => {
        if (is_dwnld === "1") {
            VideoFlashDownload(flash_data.flash_data);
        }
        const obj = { authenticate_key: "abcd123XYZ", agentId: JSON.parse(context.state.user).agentId, tourid: tourId, designid: id, orderid: "0", updateval: "0" };
        postRecord(APICreateFlashCard, obj)
            .then(res => {
                if (res.data[0].response.status === "success") {
                    //setDownloadVideo(res.data[0].response.data.file)
                    //setPermissionAllow(res.data[0].response.data.permissionAllow)
                    // setDesignId(res.data[0].response.data.designid)
                    setSync(false);
                }
                setSync(true);
            })

    }
    const downloadAndUpdate = (id, data) => {
        flashcardDatavalue.authenticate_key = "abcd123XYZ";
        flashcardDatavalue.agentId = JSON.parse(context.state.user).agentId;
        flashcardDatavalue.tourid = tourId;
        flashcardDatavalue.designid = id;
        flashcardDatavalue.orderid = "0";
        flashcardDatavalue.updateval = "0";

    }
    const createSubscribeFlashVideo = (id, flash_data, is_dwnld) => {
        if (is_dwnld === "0") {
            VideoFlashDownload(flash_data.flash_data);
        }
        const obj = { authenticate_key: "abcd123XYZ", agentId: JSON.parse(context.state.user).agentId, tourid: tourId, designid: id, orderid: "0", updateval: "0" };
        postRecord(APICreateSubscribeFlashVideo, obj)
            .then(res => {
                // if (res.data[0].response.status === "success") {
                //     setDownloadVideo(res.data[0].response.data.file)
                //     setPermissionAllow(res.data[0].response.data.permissionAllow)
                //      setDesignId(res.data[0].response.data.designid)
                //     setSync(false);
                // }
                // setSync(true);
            })
    }
    const orderAndDownload = (id, flash_data) => {
        //VideoFlashDownload(flash_data.flash_data);
        const obj = { authenticate_key: "abcd123XYZ", agentId: JSON.parse(context.state.user).agentId, tourid: tourId, designid: id, orderid: "0", updateval: "0" };
        postRecord(APICreateSubscribeFlashVideo, obj)
            .then(res => {
                // if (res.data[0].response.status === "success") {
                //     setDownloadVideo(res.data[0].response.data.file)
                //     setPermissionAllow(res.data[0].response.data.permissionAllow)
                //      setDesignId(res.data[0].response.data.designid)
                //     setSync(false);
                // }
                // setSync(true);
            })
    }
    function toDataURL(url, callback) {
        var xhr = new XMLHttpRequest();
        xhr.onload = function () {
            var reader = new FileReader();
            reader.onloadend = function () {
                callback(reader.result);
            }
            reader.readAsDataURL(xhr.response);
        };
        xhr.open('GET', url);
        xhr.responseType = 'blob';
        xhr.send();
    }
    const VideoFlashDownload = (videoDownload) => {
        toDataURL(videoDownload, function (dataUrl) {
            var link = document.createElement('a');
            link.href = dataUrl;
            link.setAttribute("download", "video.mp4");
            document.body.appendChild(link);
            link.click();
        });
        setDownloadVideo("");
    }

    const BindFlashButton = (data) => {
        if (data.btn === 0) {
            return (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '-90px' }} id='toggleHide' ><Button class='need_pic save_btn' style={{ width: '100%', margin: '10px', borderStyle: 'none', zIndex: '500' }} onClick={() => createSubscribeFlashVideo(data.id, data, "0")} >Download &amp; Update</Button></div>
            );
        }
        if (data.btn === 1) {
            return (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '-90px' }} id='toggleHide' ><Button class='need_pic save_btn' style={{ width: '100%', margin: '10px', borderStyle: 'none', zIndex: '500' }} onClick={() => createSubscribeFlashVideo(data.id, data, "1")} >Create Subscribe Flash</Button></div>
            );
        }
        if (data.btn === 2) {
            return (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '-90px' }} id='toggleHide' ><Button class='need_pic save_btn' style={{ width: '100%', margin: '10px', borderStyle: 'none', zIndex: '500' }} onClick={() => orderAndDownload(data.id, data)}  >Order &amp; Download</Button></div>
            );
        }
        if (data.btn === 3) {
            return (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '-90px' }} id='toggleHide' ><Button class='need_pic save_btn' style={{ width: '100%', margin: '10px', borderStyle: 'none', zIndex: '500' }} onClick={() => createEzFlashCard(data, data.id, "1")}>Download &amp; Update</Button></div>
            );
        }
        if (data.btn === 4) {
            return (
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '-90px' }} id='toggleHide' ><Button class='need_pic save_btn' style={{ width: '100%', margin: '10px', borderStyle: 'none', zIndex: '500' }} onClick={() => createEzFlashCard(data, data.id, "0")}>Create EZ Flash Card</Button></div>
            );
        }

    }
    return (
        <>
            <AgentHeader />
            <section class="vtc_agent_banner" style={{ backgroundImage: "url(" + banner + ")" }}>
                <div class="vtc_top_menu">
                    <div class="container-fluid">
                        <div class="row">
                            <div class="col-lg-12 col-md-12">
                                <div class="vtc_agent_menu_top">
                                    <ul>
                                        <li>
                                            <Link to={APIPath() + "agent-dashboard"}>My Cafe</Link>
                                        </li>
                                        <li>
                                            <Link to={APIPath() + "agent-image-sets"}>Image Sets</Link>
                                        </li>
                                        <li>
                                            <Link to={APIPath() + "agent-tour-list"}>Tours</Link>
                                        </li>
                                        <li >
                                            <Link to={APIPath() + "agent-flyer"}>Flyers</Link>
                                        </li>
                                        <li class="active">
                                            <Link to={APIPath() + "agent-video-list"}>Videos</Link>
                                        </li>
                                        <li>
                                            <Link to={APIPath() + "agent-setting"}>Settings</Link>
                                        </li>
                                        <li>
                                            <Link to={APIPath() + "agent-preferred-vendor"}>Preferred Vendors</Link>
                                        </li>
                                        <li>
