          <div class="row" style={{ display: "grid" }}>
            <Styles>
              <Form
                onSubmit={onSubmit}
                render={({
                  handleSubmit,
                  form,
                  submitting,
                  pristine,
                  values,
                  active,
                }) => {
                  return (
                    <form onSubmit={handleSubmit}>
                      <Card
                        number={values.number || ""}
                        name={values.name || ""}
                        expiry={values.expiry || ""}
                        cvv={values.cvc || ""}
                        focused={active}
                      />
                      <div>
                        <Field
                          name="number"
                          component="input"
                          type="text"
                          pattern="[\d| ]{16,22}"
                          placeholder="Card Number"
                          format={formatCreditCardNumber}
                        />
                      </div>
                      <div>
                        <Field
                          name="name"
                          component="input"
                          type="text"
                          placeholder="Name"
                        />
                      </div>
                      <div>
                        <Field
                          name="expiry"
                          component="input"
                          type="text"
                          pattern="\d\d/\d\d"
                          placeholder="Expiration Date"
                          format={formatExpirationDate}
                        />
                        <Field
                          style={{ width: "50px" }}
                          name="cvc"
                          component="input"
                          type="text"
                          pattern="\d{3,4}"
                          placeholder="CVV"
                          format={formatCVC}
                        />
                      </div>
                      <div className="buttons">
                        <button type="submit" disabled={submitting}>
                          Submit
                        </button>
                        <button
                          type="button"
                          onClick={form.reset}
                          disabled={submitting || pristine}
                        >
                          Reset
                        </button>
                      </div>
                    </form>
                  );
                }}
              />
            </Styles>
          </div>
          <div class="row">
            <div class="col-lg-12 mx-auto text-center">
              <a
                onClick={() => cancelOrder()}
                class="let_start"
                style={{ margin: "0px 20px 0px 0px" }}
              >
                <i
                  style={{ marginRight: "10px", marginLeft: "0px" }}
                  class="far fa-times"
                ></i>
                Cancel{" "}
              </a>
              <a
                onClick={() => {
                  saveForLater();
                  // setPackageTab(true);
                }}
                style={{ cursor: "pointer" }}
                class="let_start_new"
              >
                Save For Later{" "}
                <i
                  style={{ marginRight: "10px", marginLeft: "0px" }}
                  class="far fa-save"
                ></i>
              </a>
            </div>
          </div>
          <Backdrop className={classes.backdrop} open={open}>
            <CircularProgress color="inherit" />
          </Backdrop>
          <Snackbar
            anchorOrigin={{ vertical: "top", horizontal: "right" }}
            open={openSuccess}
            autoHideDuration={3000}
            onClose={handleClose}
          >
            <Alert onClose={handleClose} severity="success">
              {message}
            </Alert>
          </Snackbar>
          <Snackbar
            anchorOrigin={{ vertical: "top", horizontal: "right" }}
            open={openError}
            autoHideDuration={3000}
            onClose={handleClose}
          >
            <Alert onClose={handleClose} severity="error">
              {message}
            </Alert>
          </Snackbar>
        </div>
      </div>
      <button class="accordion" onClick={opencod}>
        Cash on delivery
      </button>
      <div class="panel2">
      <button class="accordion" onClick={opencreditcard}></button>

      </div>
    </div>
  );
}
