                                                                            <tr>
                                                                                <td style={{ fontSize: "12px" }}>{index + 1}</td>
                                                                                <td style={{ fontSize: "12px" }}>{res.leadcapture === 1 ? "true" : "false"}</td>
                                                                                <td style={{ fontSize: "12px" }}>{res.docname}</td>
                                                                                <td style={{ fontSize: "12px" }}>{res.filename}</td>
                                                                                <td style={{ fontSize: "12px" }}>{res.pwd}</td>
                                                                                <td style={{ fontSize: "12px" }}>
                                                                                    <Button style={{ background: "red" }} onClick={() => removeDocData(res.id)} startIcon={<CancelIcon />} variant="contained" color="primary">
                                                                                        Remove
                                                                                    </Button>
                                                                                </td>
                                                                            </tr>
                                                                        ))
                                                                    ) : (
                                                                        ""
                                                                    )}
                                                                </tbody>
                                                            </table>
                                                            {totalDivs.map((res, index) => (
                                                                <div class="row">
                                                                    <div class="col-md-2 formbox1">
                                                                        <label style={{ marginRight: "15px" }}>LEAD CAPTURE<span style={{ color: "#ffa12d" }}></span></label>
                                                                        <Switch
                                                                            readOnly
                                                                            onChange={(event) => handleLeadChange(event, index)}
                                                                            checked={documentLeadData["leadcapture" + index] ? true : false}
                                                                            //  checked={documentData[leadcapture${index}] ? true : false}
                                                                            handleDiameter={28}
                                                                            offColor="#5D5D5D"
                                                                            onColor="#F6AD17"
                                                                            offHandleColor="#fff"
                                                                            onHandleColor="#fff"
                                                                            height={35}
                                                                            width={60}
                                                                            borderRadius={6}
                                                                            uncheckedIcon={
                                                                                <div
                                                                                    style={{
                                                                                        display: "flex",
                                                                                        justifyContent: "center",
                                                                                        alignItems: "center",
                                                                                        height: "100%",
                                                                                        fontSize: 15,
                                                                                        color: "white",
                                                                                        paddingRight: 2
                                                                                    }}
                                                                                >
                                                                                    No
                                                                                </div>
                                                                            }
                                                                            checkedIcon={
                                                                                <div
                                                                                    style={{
                                                                                        display: "flex",
                                                                                        justifyContent: "center",
                                                                                        alignItems: "center",
                                                                                        height: "100%",
                                                                                        fontSize: 15,
                                                                                        color: "white",
                                                                                        paddingRight: 2
                                                                                    }}
                                                                                >
                                                                                    Yes
                                                                                </div>
                                                                            }
                                                                            className="react-switch"
                                                                        />
                                                                    </div>
                                                                    <div class="col-md-2 formbox1">
                                                                        <label>Name <span style={{ color: "#ffa12d" }}></span></label>
                                                                        <input type="text" name={"documentName" + index} value={documentNameData["documentName" + index]} onChange={(evt) => handleDocNameChange(evt, index)} class="form-control" />
                                                                    </div>
                                                                    <div style={{ marginTop: "25px" }} class="col-md-4 formbox1">
                                                                        <label style={{ marginRight: "15px" }}>File<span style={{ color: "#ffa12d" }}></span></label>
                                                                        <input type="file" accept="/*" onChange={(evt) => handleDocImageChange(evt, index)} />
                                                                        {/* <span>{documentImageData["name" + index]}</span> */}
                                                                    </div>
                                                                    <div class="col-md-2 formbox1">
                                                                        <label>Password <span style={{ color: "#ffa12d" }}></span></label>
                                                                        <input type="text" name={"documentPassword" + index} value={documentPwdData["documentPassword" + index]} onChange={(evt) => handleDocPwdChange(evt, index)} class="form-control" />
                                                                    </div>
                                                                    <div class="col-md-2 formbox1">
                                                                        <Button style={{ marginTop: "25px", background: "red" }} onClick={() => removeDiv()} startIcon={<CancelIcon />} variant="contained" color="primary">
                                                                            Remove
                                                                        </Button>
                                                                    </div>
                                                                </div>
                                                            ))}
                                                            <div class="row">
                                                                <div class="col-md-6">
                                                                    <Button style={{ marginTop: "25px" }} onClick={() => addNewDiv()} startIcon={<AddIcon />} variant="contained" color="primary">
                                                                        Add Document
                                                                    </Button>
                                                                </div>
                                                                <div class="col-md-6">
                                                                    <button type="submit" class="next_btn border-0">SAVE</button>
                                                                </div>
                                                            </div>
                                                        </form>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
            <div class="agent_pop">
                <div id="bg_music" class="modal fade" role="dialog">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header" style={{ display: "block !important" }}>
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <h4 class="modal-title">Background Music<i class="fab fa-wpforms"></i></h4>
                            </div>
                            <div class="modal-body">
                                <div class="browse_img">
                                    <div class="browse_img_conts_main">
                                        <div class="row">
                                            <div class="col-md-6" style={{ height: "500px", overflowX: "scroll" }}>
                                                <ul style={{ display: "grid" }}>
                                                    <li style={{ margin: "10px" }}> <input type="radio" id="m0" value="null" onclick="play_this(this.id)" name="mus" /><label style={{ marginLeft: "20px" }} for="m0"> None </label> </li>
                                                    {musicData && musicData.all_music ? (
                                                        musicData.all_music.map(res => (
                                                            <li style={{ margin: "10px" }}>
                                                                <input type="radio" onClick={() => handleMusicChange(res)} name="mus" value="Above_and_Beyond_full_mix" checked={currentMusic.musicid === res.musicid} />
                                                                <label style={{ marginLeft: "20px" }} for="Above_and_Beyond_full_mix.mp3">
                                                                    {res.caption}
                                                                </label>
                                                            </li>
                                                        ))
                                                    ) : (
                                                        ""
                                                    )}
                                                </ul>
                                            </div>
                                            <div class="col-md-6" style={{ margin: "150px auto", display: "block", left: "150px" }}>
                                                <audio
                                                    autoPlay
                                                    controls
                                                    src={music}>
                                                    <code>audio</code>
                                                </audio>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="modal-footer">
                                <button onClick={updateMusicData} type="button" class="btn btn-default" data-dismiss="">Save</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div class="agent_pop">
                <div id="open_house" class="modal fade" role="dialog">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header">
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <h4 class="modal-title">Open House Announcements<i class="fab fa-wpforms"></i></h4>
                            </div>
                            <div class="modal-body">
                                <div class="browse_img">
                                    <div class="browse_img_conts_main">
                                        <h6>Create Announcements</h6>
                                        <hr class=""></hr>
                                        <div class="row">
                                            <div class="col-md-4 formbox1">
                                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                                    <Grid container justifyContent="space-around">
                                                        <KeyboardDatePicker
                                                            disableToolbar
                                                            variant="inline"
                                                            format="dd/MM/yyyy"
                                                            margin="normal"
                                                            id="date-picker-inline"
                                                            label="Date"
                                                            value={announcementData.announcedate}
                                                            onChange={handleDateChange}
                                                            KeyboardButtonProps={{
                                                                'aria-label': 'change date',
                                                            }}
                                                        />
                                                    </Grid>
                                                </MuiPickersUtilsProvider>
                                            </div>
                                            <div class="col-md-4 formbox1">
                                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                                    <Grid container justifyContent="space-around">
                                                        <KeyboardTimePicker
                                                            margin="normal"
                                                            id="time-picker"
                                                            label="From"
                                                            value={announcementData.fromtime_h}
                                                            onChange={handleFromChange}
                                                            KeyboardButtonProps={{
                                                                'aria-label': 'change time',
                                                            }}
                                                        />
                                                    </Grid>
                                                </MuiPickersUtilsProvider>
                                            </div>
                                            <div class="col-md-4 formbox1">
                                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                                    <Grid container justifyContent="space-around">
                                                        <KeyboardTimePicker
                                                            margin="normal"
                                                            id="time-picker"
                                                            label="To"
                                                            value={announcementData.totime_h}
                                                            onChange={handleToChange}
                                                            KeyboardButtonProps={{
                                                                'aria-label': 'change time',
                                                            }}
                                                        />
                                                    </Grid>
                                                </MuiPickersUtilsProvider>
                                            </div>
                                        </div>
                                        <div class="row">
                                            <div class="col-md-12">
                                                {editing ? (
                                                    <button onClick={updateAnnouncement} style={{ float: "right" }} type="button" class="next_btn border-0 mr-1">Update</button>
                                                ) : (
                                                    <button onClick={saveAnnouncement} style={{ float: "right" }} type="button" class="next_btn border-0 mr-1">Save</button>
                                                )}

                                            </div>
                                        </div>
                                        <h6>Announcements</h6>
                                        <hr class=""></hr>
                                        {announcements.length > 0 ? (

                                            <table id="announcementTable" class="table table-bordered">
                                                <tbody>
                                                    <tr>
                                                        <td><b>Announce date</b></td>
                                                        <td><b>From</b></td>
                                                        <td><b>To</b></td>
                                                        <td><b>Action</b></td>

                                                    </tr>
                                                    {announcements.map(res => (
                                                        <tr>
                                                            <td valign="top">{dateFormat(res.announcedate, "dd-mm-yyyy")}</td>
                                                            <td valign="top">{res.fromtime + " " + res.fromampm}</td>
                                                            <td valign="top">{res.totime + " " + res.toampm}</td>
                                                            <td valign="top" >
                                                                <Button
                                                                    style={{ padding: "4px" }}
                                                                    variant="contained"
                                                                    color="primary"
                                                                    onClick={() => editAnnouncement(res)}
                                                                >
                                                                    Edit
                                                                </Button>
                                                                <Button
                                                                    style={{ marginLeft: "10px", padding: "4px" }}
                                                                    variant="contained"
                                                                    color="secondary"
                                                                    onClick={() => deleteAnnouncement(res)}
                                                                >
                                                                    Delete
                                                                </Button>

                                                            </td>
                                                        </tr>
                                                    ))}

                                                </tbody>
                                            </table>

                                        ) : (
                                            ""
                                        )}
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </div>

            <div class="agent_pop">
                <div id="Newsletter" class="modal fade" role="dialog">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header">
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <h4 class="modal-title">Add NewsLetter<i class="fab fa-wpforms"></i></h4>
                            </div>
                            <div class="modal-body">
                                <div class="browse_img">
                                    <div class="browse_img_conts_main">
                                        <form onSubmit={event => {
                                            event.preventDefault();
                                            saveNewsLetter();
                                        }}>
                                            <div class="row">
                                                <div class="col-md-12 formbox1">
                                                    <label>Forms<span style={{ color: "#ffa12d" }}>*</span></label>
                                                    <textarea onChange={handleNewsChange} value={newsLetterData.text} name="text" class="form-control" rows="5"></textarea>
                                                </div>
                                            </div>
                                            <div class="row">
                                                <div class="col-md-12">
                                                    <button type="submit" class="next_btn border-0 mr-1">Save</button>
                                                    <button type="button" class="next_btn grey border-0" onClick={() => setNewsLetterData(initialNewsLetterData)}>Reset</button>
                                                </div>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                            {/* <div class="modal-footer">
                                <button type="button" class="btn btn-default" data-dismiss="">Save</button>
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
            <div class="agent_pop">
                <div id="facebook" class="modal fade" role="dialog">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header">
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <h4 class="modal-title">Post to Facebook<i class="fab fa-wpforms"></i></h4>
                            </div>
                            <div class="modal-body">
                                <div class="browse_img">
                                    <div class="browse_img_conts_main">
                                        <form onSubmit={event => {
                                            event.preventDefault();
                                            //saveNewsLetter();
                                        }}>
                                            <div class="row">
                                                <div class="col-md-12">
                                                    <div class="card">
                                                        <div class="card-body">
                                                            If you are a Macintosh User, please log in to your Facebook account first, then click on "Continue" button below.
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="row">
                                                <div class="col-md-12">
                                                    <button onClick={facebookLoginLink} class="next_btn border-0 mr-1">Continue</button>
                                                </div>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openSuccess} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="success">
                    {message}
                </Alert>
            </Snackbar>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openError} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="error">
                    {message}
                </Alert>
            </Snackbar>
            <Backdrop className={classes.backdrop} open={open}>
                <CircularProgress color="inherit" />
            </Backdrop>
        </div>
    )
}
