                                                    <li class="">
                                                        <a onClick={handleEditModal}><span><i class="fas fa-pen"></i></span>Edit Selected Agents</a>
                                                    </li>
                                                    <li class="">
                                                        {/* <a href="#"><span><i class="far fa-eye"></i></span>View Tour</a> */}
                                                        <a onClick={handleDeleteModal}><span><i class="fas fa-trash-alt"></i></span>Delete Selected Agents</a>
                                                    </li>
                                                    <li class="">
                                                        <a onClick={() => LoginSelectedAgent()}><span><i class="fa fa-sign-in" aria-hidden="true"></i></span>Login Selected Agents</a>
                                                    </li>
                                                    <li class="">
