                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <input type="text" name="searchtxt" class="form-control" value={propertyData.searchtxt} placeholder="Name" onChange={inputHandleChange} />
                                                    </div>
                                                </div>
