                                                    <div class="filter_sec_single">
                                                        <input type="text" name="email"
                                                            value={propertyData.email}
                                                            class="form-control" placeholder="email" onChange={inputHandleChange} />
                                                    </div>
                                                </div>
                                                {/* <div class="col-lg-3 col-md-3">
                                                <div class="filter_sec_single">
                                                    <select class="form-control formbox1select">
                                                        <option>Select State</option>
                                                        <option>Select State</option>
                                                        <option>Select State</option>
                                                        <option>Select State</option>
                                                        <option>Select State</option>
                                                    </select>
                                                </div>
                                            </div> */}
                                                {/* <div class="col-lg-3 col-md-3">
                                                <div class="filter_sec_single">
                                                    <input type="text" name="" class="form-control" placeholder="Zip Code" />
                                                </div>
                                            </div> */}
                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <input type="tel" name="mobile" value={propertyData.mobile} class="form-control" maxLength="12" minLength="10" placeholder="phone" onChange={inputHandleChange} />
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="row padd_top">
                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <input type="tel" name="city" value={propertyData.city} class="form-control" placeholder="City" onChange={inputHandleChange} />
                                                    </div>
                                                </div>
                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <select
                                                            name="countryid"
                                                            value={propertyData.countryid}
                                                            onChange={handleInputSelectChange}
                                                            class="form-control formbox1select"
                                                        >
                                                            <option value="0">Select Country</option>
                                                            {allCountries.map(res => (
                                                                <option value={res.id}>{res.name}</option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                </div>
                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <select
                                                            name="stateid"
                                                            value={propertyData.stateid}
                                                            onChange={handleInputSelectChange}
                                                            class="form-control formbox1select"
                                                        >
                                                            <option value="0">Select State</option>
                                                            {allStates && allStates.map(res => (
                                                                <option value={res.id}>{res.name}</option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                </div>
                                                <div class="col-lg-3 col-md-3">
                                                    <div class="filter_sec_single">
                                                        <div class="row">
                                                            <div class="col-lg-6 col-md-6">
                                                                <input type="text" name="zipcode" value={propertyData.zipcode} class="form-control" placeholder="Zip Code" onChange={inputHandleChange} />
                                                            </div>
                                                            <div class="col-lg-6 col-md-6">
                                                                <button type="submit" class="next_btn">FILTER</button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col-lg-12 col-md-12">
                            <div class="profile_listing_main">
                                <div class="row">
                                    {Object.keys(allData).length > 0 ? (
                                        allData.map(res => (
                                            // <h2>{res.username}</h2>
                                            <div onClick={() => {
                                                setId(res.id);
                                                handleImageSetId(res);
                                            }} class="col-lg-4 col-md-4">
                                                <div id={"myDiv" + res.id} class="profile_listing_single" style={{ cursor: "pointer" }}>
                                                    <div class="profile_listing_single_inner">
                                                        <div class="socila_avatar">
                                                            <div class="socila_avatar_img">
                                                                <img class="" alt="image" src={res.src} />
                                                            </div>
                                                            <div class="socila_avatar_cont">
                                                                <h6>{res.fname} {res.lname}</h6>
                                                                <p>Active Tour:{res.is_autotour}</p>
                                                                <p>Company Name :{res.company}</p>
                                                                <p class="">{res.address} </p>
                                                                <p>Email:{res.email}</p>
                                                            </div>
                                                        </div>
                                                        <div class="socila_body">
                                                            <div class="socila_body_btn">
                                                                <button onClick={() => handleEditAgent(res)}><i class="far fa-edit"></i>Edit</button>
                                                                <button onClick={() => handleDelete(res.id)}><i class="far fa-trash-alt"></i>Delete</button>
                                                            </div>
                                                            <div class="socila_body_icn">
                                                                <ul>
                                                                    <li><label>Share:</label><a href="#"><i class="fab fa-facebook-square fb"></i></a></li>
                                                                    <li><a href="#"><i class="fab fa-twitter-square tw"></i></a></li>
                                                                </ul>
                                                            </div>
                                                        </div>
                                                        <div class="row">
                                                            <div class="col-lg-6 col-md-6">
                                                                <Button style={{ backgroundColor: "#ffa12d" }}><i class="fa fa-sign-out" aria-hidden="true"></i>Agent Has not Logged In</Button>
                                                            </div>
                                                            <div class="col-lg-6 col-md-6">
                                                                <Button onClick={() => LoginSelectedAgent()}><span><i class="fa fa-sign-in"></i></span>Login For This Agent</Button>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))
                                    ) : (
                                        ""
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                    <hr class="spacer10px"></hr>
                    <hr class="spacer10px"></hr>
                    <div class="row">
                        <div class="col-lg-12">
                            <ReactPaginate
                                previousLabel={"«"}
                                nextLabel={"»"}
                                breakLabel={"..."}
                                pageCount={pageCount}
                                marginPagesDisplayed={3}
                                ageRangeDisplayed={3}
                                onPageChange={handlePageClick}
                                containerClassName="pagination justify-content-center"
                                pageClassName='page-item'
                                pageLinkClassName='page-link'
                                previousClassName='page-item'
                                previousLinkClassName='page-link'
                                nextClassName='page-item'
                                nextLinkClassName='page-link'
                                breakClassName='page-item'
                                breakLinkClassName='page-link'
                                activeClassName='active'
                            />
                        </div>
                    </div>

                </div>
            </section>
            <BrokerFooter />
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openWarning} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="warning">
                    {message}
                </Alert>
            </Snackbar>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openError} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="error">
                    {message}
                </Alert>
            </Snackbar>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openSuccess} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="success">
                    {message}
                </Alert>
            </Snackbar>
            <Backdrop className={classes.backdrop} open={open}>
                <CircularProgress color="inherit" />
            </Backdrop>
            <Dialog maxWidth={maxWidth} fullWidth={true} onClose={handleClose} aria-labelledby="customized-dialog-title" open={openCreateAgents}>
                <DialogTitle id="customized-dialog-title" >
                    Create Agent
                    <CancelIcon onClick={() => setOpenCreateAgents(false)} style={{ float: "right", cursor: "pointer" }} />
                </DialogTitle>
                <DialogContent dividers>
                    <div class="container">
                        <form
                            onSubmit={event => {
                                event.preventDefault();
                                validateinp();
                                saveAgentData();
                            }}>
                            <div class="agent_pop_main">
                                <div class="row">
                                    <div class="col-lg-12 col-md-12">
                                        <label style={{ marginBottom: "10px", marginTop: "10px" }}>First Name </label>
                                        <input type="text" name="fname" value={agentData.fname} onChange={handleInputChange} onKeyUp={(e) => e.target.value.length > 0 ? (e.target.classList.add('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            fname: '',
                                            fname_error: ''
                                        }))) : (e.target.classList.remove('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            fname: 'error',
                                            fname_error: 'First name cannot be blank'
                                        })))} className={inputErrors.fname == 'error' ? 'form-control inperror' : 'form-control'} />
                                        <p style={{ color: 'red' }}>{inputErrors.fname_error}</p>
                                    </div>
                                    <div class="col-lg-12 col-md-12">
                                        <label style={{ marginBottom: "10px", marginTop: "10px" }}> Last Name </label>
                                        <input type="text" name="lname" value={agentData.lname} onKeyUp={(e) => e.target.value.length > 0 ? (e.target.classList.add('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            lname: '',
                                            lname_error: ''
                                        }))) : (e.target.classList.remove('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            lname: 'error',
                                            lname_error: 'Last name cannot be blank'
                                        })))} onChange={handleInputChange} className={inputErrors.lname !== '' ? 'form-control inperror' : 'form-control'} />
                                        <p style={{ color: 'red' }}>{inputErrors.lname_error}</p>

                                    </div>
                                    <div class="col-lg-12 col-md-12">
                                        <label style={{ marginBottom: "10px", marginTop: "10px" }}>Email </label>
                                        <input type="email" name="email" onBlur={(e) => checkEmail(e)} value={agentData.email} onChange={handleInputChange} onKeyUp={(e) => regex.test(e.target.value) ? (e.target.classList.add('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            email: '',
                                            email_error: ''
                                        }))) : (e.target.classList.remove('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            email_error: 'Please Enter A Valid Email'
                                        })))} className={inputErrors.email !== '' ? 'form-control inperror' : 'form-control'} />
                                        <p style={{ color: 'red' }}>{inputErrors.email_error}</p>

                                    </div>
                                    <div class="col-lg-12 col-md-12">
                                        <label style={{ marginBottom: "10px", marginTop: "10px" }}>Confirm Email </label>
                                        <input type="email" name="cnfemail" value={agentData.cnfemail} onChange={handleInputChange} onKeyUp={(e) => e.target.value === agentData.email ? (e.target.classList.add('success'), e.target.classList.remove('inperror'), setinputErrors(prevState => ({
                                            ...prevState,
                                            cnfemail: '',
                                            cnfemail_error: ''
                                        }))) : (e.target.classList.remove('success'), setinputErrors(prevState => ({
                                            ...prevState,
                                            cnfemail: 'error',
                                            cnfemail_error: 'E-Mail Confirmation  Does Not match'
                                        })))} className={inputErrors.cnfemail !== '' ? 'form-control inperror' : 'form-control'} />
                                        <p style={{ color: 'red' }}>{inputErrors.cnfemail_error}</p>

                                    </div>
                                    <div class="col-lg-12 col-md-12">
                                        <input type="checkbox" name="emailChk" value={agentData.emailChk} onChange={handleCheckBoxChange} style={{ marginTop: "10px" }} /> Do Not Send Welcome Email
                                    </div>
                                </div>
                            </div>
                            <div class="row">
                                <div class="col-lg-12 col-md-12 text-center">
                                    <button style={{ border: "#ffa124" }} type="submit" class="need_pic save_btn">Save<i class="fas fa-arrow-right"></i></button>
                                </div>
                            </div>
                        </form>
                    </div>
                </DialogContent>
            </Dialog>
        </div>

    )
}
