                                <div class="popup_tour_main">
                                    <div class="popup_tour">
                                        <div class="popup_tour_head">
                                            <h5>Tour</h5>
                                        </div>
                                    </div>
                                    <div class="dist_tour_cont">
                                        <div class="dist_tour_cont_socila">
                                            <h6 class="social_head">Be Social-Share Your Tour</h6>
                                            <div class="blog_left_inner_share inner_blog">
                                                <ul>
                                                    <li><a target="_blank" href={distributeData && distributeData.facebookLink}><i class="fab fa-facebook-f"></i></a></li>
                                                    <li><a target="_blank" href={distributeData && distributeData.twitterLink}><i class="fab fa-twitter"></i></a></li>
                                                    <li><a target="_blank" href={distributeData && distributeData.shareArticle}><i class="fab fa-linkedin-in"></i></a></li>
                                                    <li><a target="_blank" href={distributeData && distributeData.pinitSharee}><i class="fab fa-pinterest-p"></i></a></li>
                                                </ul>
                                            </div>
                                        </div>
                                        <div class="dist_tour_cont_socila_copy">
                                            <h6 class="social_head">Copy & Paste Links to:</h6>
                                            <div class="dist_tour_cont_socila_copy_cont">
                                                <p>Zillow Group (Zillow/Trulia/HotPads), Homes.com and Others.</p>
                                                <div class="table_sec">
                                                    <table style={{ width: "100%" }}>
                                                        <tbody>
                                                            <tr>
                                                                <td style={{ width: "20%" }}>Branded Tour Link:</td>
                                                                <td style={{ width: "60%" }}><input readonly="true" type="text" value={distributeData && distributeData.BrandedTourLink} /></td>
                                                                <td style={{ textAlign: "center" }}> <button class="next_btn">Copy</button></td>
                                                            </tr>
                                                        </tbody>
                                                    </table>
                                                </div>
                                                <p>Multple Listng Service (MLS)</p>
                                                <div class="table_sec">
                                                    <tbody>
                                                        <tr>
                                                            <td style={{ width: "20%" }}>Un-Branded Tour Link:</td>
                                                            <td style={{ width: "60%" }}><input readonly="true" type="text" value={distributeData && distributeData.n_BrandedTourLink} /></td>
                                                            <td style={{ textAlign: "center" }}> <button class="next_btn">Copy</button></td>
                                                        </tr>
                                                    </tbody>
                                                </div>
                                            </div>
                                        </div>
                                        <div class="popup_tour_head">
                                            <h5>Video</h5>
                                        </div>
                                        <div class="socila_status">
                                            <div class="row">
                                                <div class="col-lg-12 col-md-12">
                                                    <div class="socila_status_single">
                                                        <label>Distribute Video</label>
                                                        <div class="switchToggle custom-control custom-switch">
                                                            <input type="checkbox" class="custom-control-input" id="customSwitch1114" />
                                                            <label class="custom-control-label" for="customSwitch1114">&nbsp;</label>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-default" data-dismiss="">Save</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div class="agent_pop">
                <div id="facebook" class="modal fade" role="dialog">
                    <div class="modal-dialog">
                        <div class="modal-content">
                            <div class="modal-header">
                                <button type="button" class="close" data-dismiss="modal">&times;</button>
                                <h4 class="modal-title">Post to Facebook<i class="fab fa-wpforms"></i></h4>
                            </div>
                            <div class="modal-body">
                                <div class="browse_img">
                                    <div class="browse_img_conts_main">
                                        <form onSubmit={event => {
                                            event.preventDefault();
                                            //saveNewsLetter();
                                        }}>
                                            <div class="row">
                                                <div class="col-md-12">
                                                    <div class="card">
                                                        <div class="card-body">
                                                            If you are a Macintosh User, please log in to your Facebook account first, then click on "Continue" button below.
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="row">
                                                <div class="col-md-12">
                                                    <button class="next_btn border-0 mr-1">Continue</button>
                                                </div>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                            {/* <div class="modal-footer">
                                <button type="button" class="btn btn-default" data-dismiss="">Save</button>
                            </div> */}
                        </div>
                    </div>
                </div>
            </div>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openSuccess} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="success">
                    {message}
                </Alert>
            </Snackbar>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openError} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="error">
                    {message}
                </Alert>
            </Snackbar>
            <Backdrop className={classes.backdrop} open={open}>
                <CircularProgress color="inherit" />
            </Backdrop>
        </div>
    );
}
