                                    <div class="col-lg-6 col-md-6">
                                        <h6>Custom Banner</h6>
                                        {customBannerImg === "" ? (
                                            <img src={defaultBanner} alt="" style={{ marginBottom: "10px" }} />
                                        ) : (
                                            <img src={customBannerImg} alt="" style={{ marginBottom: "10px", width: "100%", height: "80px" }} />
                                        )}
                                        <input type="file" accept="image/*" onChange={handleImageChange} />
                                        <button onClick={() => removeBanner()} style={{ border: "#ffa124", padding: "10px", float: "right", background: "#bbbbbb", color: "#000" }} type="button" class="">Remove</button>
                                    </div>
                                    <div class="col-lg-6 col-md-6 formbox1">
                                        <h6>Offered Banners</h6>
                                        {offeredBannerImg === "" ? (
                                            <img src={defaultBanner} alt="" style={{ marginBottom: "10px" }} />
                                        ) : (
                                            <img src={offeredBannerImg} alt="" style={{ marginBottom: "10px", width: "100%", height: "80px" }} />
                                        )}

                                        <select value={headerImageId} onChange={handleBannerChange} class="form-control formbox1select">
                                            <option value="0">None</option>
                                            {allBanners.map(res => (
                                                <option value={res.id}>{res.imagename}</option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>
                            <div class="row">
                                <div class="col-lg-12 col-md-12 text-center">
                                    <button style={{ border: "#ffa124" }} type="submit" class="need_pic save_btn">Submit<i class="fas fa-arrow-right"></i></button>
                                </div>
                            </div>
                        </form>
                    </div>
                </DialogContent>
            </Dialog>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openSuccess} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="success">
                    {message}
                </Alert>
            </Snackbar>
            <Snackbar anchorOrigin={{ vertical: "top", horizontal: "right" }} open={openError} autoHideDuration={3000} onClose={handleClose}>
                <Alert onClose={handleClose} severity="error">
                    {message}
                </Alert>
            </Snackbar>
            <Backdrop className={classes.backdrop} open={open}>
                <CircularProgress color="inherit" />
            </Backdrop>
        </div>
    )
}
