    return (
        <div class="appointment_page_right">
            <div class="row">
                <div class="col-md-12">
                    <h3>Ckeckout</h3>
                    <div class="step_progress">
                        <span>Step 4/4</span>
                        <div class="progress" style={{ height: "20px" }}>
                            <div class="progress-bar" role="progressbar" style={{ width: "100%" }} aria-valuenow="33" aria-valuemin="0" aria-valuemax="100">

                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <button class="accordion" onClick={opencreditcard}>Credit Card</button>
<div class="panel">
            <div class="Order_cont">
                <div class="row">
                    <table width="100%" border="0" cellspacing="5" cellpadding="5">
                        <tbody>
                            <tr>
                                <td width="60%">Order Total</td>
                                <td width="40%" align="right"><strong>$ {price + ".00"}</strong></td>
                            </tr>
                            <tr>
                                <td>Estimated Tax</td>
                                <td align="right"><strong>$0.00</strong></td>
                            </tr>
                            <tr>
                                <td colspan="2" height="5px"></td>
                            </tr>
                            <tr>
                                <td colspan="2" bgcolor="#dedede" height="3px" style={{ padding: "0" }}></td>
                            </tr>
                            <tr>
                                <td colspan="2" height="5px"></td>
                            </tr>
