                                setFinalPreviewtab(false);
                                setPropertyTab(true);
                            }}><i class="fas fa-edit"></i></a></span>
                        </div>
                    </div>
                </div>
                <div class="row">
                    <table width="100%" border="0" cellspacing="0" cellpadding="0">
                        <tbody>
                            <tr>
                                <td width="20%"><b>Street Address:</b></td>
                                <td width="30%">{basicInfo.address}</td>
                                <td width="20%"><b>First Choice :</b></td>
                                <td width="30%">{dateFormat(propertyInfo.first_choice, "dd-mm-yyyy") + "  " + propertyInfo.first_time}</td>
                            </tr>
                            <tr>
                                <td colspan="4">&nbsp;</td>
                            </tr>
                            <tr>
                                <td width="20%"><b>City:</b></td>
                                <td width="30%">{basicInfo.city}</td>
                                <td width="20%"><b>Second Choice :</b></td>
                                <td width="30%">{dateFormat(propertyInfo.second_choice, "dd-mm-yyyy") + "  " + propertyInfo.second_time}</td>
