                            </tr>
                            <tr>
                                <td width="20%"><b>State:</b></td>
                                <td width="30%">{basicInfo.state}</td>
                                <td width="20%"><b>Interior Sq Ft:</b></td>
                                <td width="30%">{basicInfo.interior_area}</td>
