                <div class="row">
                    <div class="col-lg-12 col-md-12 mx-auto text-center">
                        <a onClick={() => {
                            setFinalPreviewtab(false);
                            setPropertyTab(true);
                        }} class="let_start" style={{ margin: "0px 20px 0px 0px" }}><i style={{ marginRight: "10px", marginLeft: "0px" }} class="fas fa-arrow-left"></i>Back </a>
